mapper.select:
select * from (select count(c.category_num) as count,c.category_num as title,@rownum:=@rownum + 1 as rnum from (SELECT @rownum:=0) TMP,   orders as o join order_bookList as b on o.orders_seq=b.orders_seq join book bo on b.book_seq=bo.book_seq join category c on c.category_num=bo.category_num 


		AND date_format(o.orders_order_day,'%m')=#{month} 
		AND date_format(o.orders_order_day,'%Y')=#{year} 



group by c.category_num order by count asc) dz where rnum<=5

select * from (select count(b.book_seq) as count,b.book_title as titlestr,@rownum:=@rownum + 1 as rnum  from (SELECT @rownum:=0) TMP,order_bookList as ob join book as b on b.book_seq=ob.book_seq join orders as ord on ord.orders_seq=ob.orders_seq 

			AND date_format(ord.orders_order_day,'%m')=#{month} 
			AND date_format(ord.orders_order_day,'%Y')=#{year} 

 group by b.book_seq) EQ where rnum<=5 order by count asc

select count(*) as count from orders where orders_status like
concat('%','cancel','%') 
AND date_format(orders_order_day,'%Y-%m-%d')=#{date}
