mapper.select:
SELECT * FROM(SELECT FAQ_seq, FAQ_bigsort, FAQ_smallsort, FAQ_ask, @rownum:=@rownum + 1 as rnum FROM FAQ, (SELECT @rownum:=0) TMP

		AND FAQ_bigsort = #{bigsort}
		AND FAQ_smallsort = #{smallsort}
		AND FAQ_ask LIKE CONCAT('%', #{ask}, '%')

ORDER BY FAQ_seq DESC) B WHERE

	rnum = #{seq}


	 rnum > (#{page} - 1) * 5 AND rnum <= #{page} * 5 ORDER BY FAQ_seq DESC

SELECT COUNT(*) FROM(SELECT FAQ_seq, FAQ_bigsort, FAQ_smallsort, FAQ_ask, @rownum:=@rownum + 1 as rnum FROM FAQ, (SELECT @rownum:=0) TMP

		AND FAQ_bigsort = #{bigsort}
		AND FAQ_smallsort = #{smallsort}
		AND FAQ_ask LIKE CONCAT('%', #{ask}, '%')

ORDER BY FAQ_seq DESC) B


			rnum = #{seq}
