mapper.select:
	SELECT COUNT(*) FROM(SELECT notice_seq, notice_title, notice_writer, notice_content, notice_date, @rownum:=@rownum + 1 as rnum FROM notice, (SELECT @rownum:=0) TMP

	AND notice_title LIKE CONCAT('%', #{title}, '%')
	AND notice_content LIKE CONCAT('%', #{content}, '%')
	AND notice_date >= #{startDate} AND notice_date <= #{endDate} 

ORDER BY notice_date DESC) B


 			rnum = #{seq}
