mapper.select:
select * from(select *, @rownum:=@rownum+1 as rnum from book  , (SELECT @rownum:=0) TMP 

		AND book_title like CONCAT('%',#{title},'%')
		AND category_num =#{category}
		AND category_num >200
		AND category_num <200
		AND author_seq=#{author_seq}

	) B where rnum>(#{page}-1)*5 AND rnum<=#{page}*5 order by book_seq
