mapper.select:
select * from (select *,@rownum:=@rownum+1 as rnum from points, (SELECT @rownum:=0) TMP where user_id=#{user_id}


		AND points_count >0

		AND points_count< 0
		AND points_date>#{points_date}


	order by points_seq desc) B where rnum>(#{page}-1)*5 AND rnum<=#{page}*5

select count(*) from (select *,@rownum:=@rownum+1 as rnum from points, (SELECT @rownum:=0) TMP where user_id=#{user_id} 

		AND points_count >0

		AND points_count< 0
		AND points_date>#{points_date}

	) B
