mapper.select:
SELECT * FROM (SELECT @rownum:=@rownum+1 AS rnum, book.* FROM book,(SELECT @rownum:=0) TMP
AND book_title LIKE CONCAT('%', #{Hthing}, '%') AND book_author LIKE CONCAT('%', #{Hthing}, '%') AND category_num LIKE CONCAT('%', #{Hthing}, '%')
) tnum where rnum>=(#{Hpage}*20-19) and rnum<=(#{Hpage}*20)
