mapper.select:
	select * from (select * ,@rownum:=@rownum+1 as rnum from (select o.orders_seq,user_id,orders_order_day,book_title,orders_status,order_bookList_seq,order_bookList_count,order_bookList_count*book_price as sum from orders as o join order_bookList as b  on o.orders_seq=b.orders_seq join book bo on b.book_seq=bo.book_seq where orders_status not like concat("%","cancel","%")
AND o.orders_seq= #{searchcontentint}
		AND o.user_id like concat('%',#{searchcontent},'%')
		AND o.orders_order_day>=#{searchcontent}
		AND book_title like concat('%',#{searchcontent},'%')

		AND o.orders_status like concat('%',#{searchcontent},'%')

		) M,(SELECT @rownum:=0) TMP 		
		where sum>=#{searchcontentint} 
		order by orders_order_day desc) rn where rnum > (#{page} - 1) * 5 AND rnum <= #{page} * 5

	select count(*) from (select * ,@rownum:=@rownum+1 as rnum from (select o.orders_seq,user_id,orders_order_day,book_title,orders_status,order_bookList_count,order_bookList_count*book_price as sum from orders as o join order_bookList as b  on o.orders_seq=b.orders_seq join book bo on b.book_seq=bo.book_seq where orders_status not like concat("%","cancel","%")
AND o.orders_seq= #{searchcontentint}
		AND o.user_id like concat('%',#{searchcontent},'%')
		AND o.orders_order_day>=#{searchcontent}
		AND book_title like concat('%',#{searchcontent},'%')

		AND o.orders_status like concat('%',#{searchcontent},'%')

) M,(SELECT @rownum:=0) TMP 		
		where sum>=#{searchcontentint} 
		order by orders_order_day desc) rn

select * from (select * ,@rownum:=@rownum+1 as rnum from (select o.orders_seq,order_bookList_seq,user_id,orders_order_day,book_title,orders_status,order_bookList_count,order_bookList_count*book_price as sum from orders as o join order_bookList as b  on o.orders_seq=b.orders_seq join book bo on b.book_seq=bo.book_seq where orders_status like concat("%","cancel","%")

	AND o.orders_seq= #{searchcontentint}
	AND o.user_id like concat('%',#{searchcontent},'%')
	AND o.orders_order_day>=#{searchcontent}
	AND book_title like concat('%',#{searchcontent},'%')

	AND o.orders_status like concat('%',#{searchcontent},'%')

) M,(SELECT @rownum:=0) TMP 		
	where sum>=#{searchcontentint} 
	order by orders_order_day desc) rn where rnum > (#{page} - 1) * 5 AND rnum <= #{page} * 5

select count(*) from (select * ,@rownum:=@rownum+1 as rnum from (select o.orders_seq,user_id,orders_order_day,book_title,orders_status,order_bookList_count,order_bookList_count*book_price as sum from orders as o join order_bookList as b  on o.orders_seq=b.orders_seq join book bo on b.book_seq=bo.book_seq where orders_status like concat("%","cancel","%")

	AND o.orders_seq= #{searchcontentint}
	AND o.user_id like concat('%',#{searchcontent},'%')
	AND o.orders_order_day>=#{searchcontent}
	AND book_title like concat('%',#{searchcontent},'%')

	AND o.orders_status like concat('%',#{searchcontent},'%')

) M,(SELECT @rownum:=0) TMP 		
	where sum>=#{searchcontentint} 
	order by orders_order_day desc) rn
